def rbfs(n, path, g, f_lim):
    if n == goal:
        return path, g, True
    succ = sorted([(g + c + heur.get(v, 0), v, g + c) for v, c in graph.get(n, []) if v not in path])
    if not succ:
        return None, float('inf'), False
    while succ:
        f, s, newg = succ[0]
        if f > f_lim:
            return None, f, False
        alt = succ[1][0] if len(succ) > 1 else float('inf')
        res, nf, found = rbfs(s, path + [s], newg, min(f_lim, alt))
        if found:
            return res, nf, True
        succ[0] = (nf, s, newg)
        succ.sort()
    return None, float('inf'), False

def dls(node, goal, depth, path, cost):
    if node == goal:
        return path, cost
    if depth ==0:
        return None, 0
    for nei, c in graph.get(node, []):
        if nei not in path:
            res, total = dls(nei, goal, depth - 1, path + [nei], cost + c)
            if res: return res, total
    return None, 0

def greedy_bfs(start, goal):
    visited = set()
    queue = [(heuristics[start], start, [start], 0)]  # (heuristic, node, path, cost)

    while queue:
        _, node, path, cost = heapq.heappop(queue)
        if node == goal:
            return path, cost
        visited.add(node)
        for neighbor, edge_cost in graph.get(node, []):
            if neighbor not in visited:
                heapq.heappush(queue, (heuristics[neighbor], neighbor, path + [neighbor], cost + edge_cost))
    return None, 0

